  `img` varchar(255) DEFAULT NULL COMMENT '书籍图片',
  `author` varchar(255) DEFAULT NULL COMMENT '书籍作者',
  `create_time` int(11) DEFAULT NULL COMMENT '书籍发布日期',
  `update_time` int(11) DEFAULT NULL COMMENT '书籍更新日期',
  `click_num` int(11) DEFAULT NULL COMMENT '书籍浏览量',
  `click_good` int(11) DEFAULT NULL COMMENT '点赞数',
  `collection` int(11) DEFAULT NULL COMMENT '收藏数',
  `cid` int(11) DEFAULT NULL COMMENT '所属分类',
  `is_hot` tinyint(1) DEFAULT NULL COMMENT '1是；2否',
  `status` tinyint(1) DEFAULT NULL COMMENT '1正常；2删除'
) ENGINE=InnoDB AUTO_INCREMENT=31 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

